import UIKit

protocol HomePresentationLogic
{
    
    // Handle API response
    func presentPropertyList(response: Home.PropertyList.Response)
    
    // Call Core data
    func presentFavoriteList(response: Home.FavoriteList.Response)
}

class HomePresenter: HomePresentationLogic
{
    
    weak var viewController: HomeDisplayLogic?
    
    // MARK: API response
    
    func presentPropertyList(response: Home.PropertyList.Response) {
        
        // Check for errors
        if response.networkError != nil {
            viewController?.displayPropertyListAPIError(error: Home.PropertyList.Error(error: response.networkError ?? .unknownError))
        }
        
        // Handle empty response
        else if response.result ?? "" == "" {
            viewController?.displayPropertyListAPIError(error: Home.PropertyList.Error(error: .noResponseData))
        }
        
        // Handle successful response
        else {
            
            // Generating JSON data
            guard let data = (response.result ?? "").data(using: .utf8, allowLossyConversion: false) else {
                viewController?.displayPropertyListAPIError(error: Home.PropertyList.Error(error: .badResponse))
                return
            }
            
            // Decode data into model
            do {
                let decoder = JSONDecoder()
                let apiResponseModel = try decoder.decode(BaseApiListModel<PropertyModel>.self, from: data)
                
                // Return result
                viewController?.displayPropertyList(viewModel: Home.PropertyList.ViewModel(proppertyList: apiResponseModel.items ?? [PropertyModel]()))
            }
            catch {
                
                // Catch decoding error
                viewController?.displayPropertyListAPIError(error: Home.PropertyList.Error(error: .decodingFailed))
                return
            }
            
        }
    }
    
    // MARK: Core Data response
    
    func presentFavoriteList(response: Home.FavoriteList.Response) {
        if response.error != nil {
            
            // Call view controller display error
            viewController?.displayFavoriteListError(error: Home.FavoriteList.Error(error: response.error!))
        } else {
            
            // Call successful view controller
            viewController?.displayFavoriteList(viewModel: Home.FavoriteList.ViewModel(favoriteIdList: response.result ?? [Int]()))
        }
    }
}
